mapper.insert:
INSERT INTO MEMBERS (
	MEMBER_ID, PW, MEMBER_NAME, EMAIL, PHONE,
	MEMBER_ROLE, COMPANY, DEPARTMENT
) VALUES (
	#{memberId}, #{pw}, #{memberName}, #{email}, #{phone},
	#{memberRole}, #{company}, #{department}
)

mapper.select:
SELECT *
FROM MEMBERS
WHERE MEMBER_ID = #{memberId}
   OR EMAIL = #{email}
   OR PHONE = #{phone}
