mapper.insert:
INSERT INTO MEMBER (
	MEMBER_ID, PASSWORD, NAME, EMAIL, PHONE,
	ROLE, COMPANY, DEPARTMENT
) VALUES (
	#{memberId}, #{password}, #{name}, #{email}, #{phone},
	#{role}, #{company}, #{department}
)

mapper.select:
SELECT *
FROM MEMBER
WHERE MEMBER_ID = #{memberId}
   OR EMAIL = #{email}
   OR PHONE = #{phone}
